data['accounts'] = accounts
date_and_suffix = 'date:%s' % report_type_suffix read_group_data = env['account.move.line'].sudo().read_group( domain=[('analytic_account_id','in', accounts.ids)], fields=['analytic_account_id','balance','date'], groupby=['analytic_account_id',date_and_suffix], lazy=False, orderby=date_and_suffix
